import { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const BookFlight = () => {
  const { id } = useParams();

  const [flightName, setFlightName] = useState('');
  const [flightId, setFlightId] = useState('');
  const [basePrice, setBasePrice] = useState(0);
  const [startCity, setStartCity] = useState('');
  const [destinationCity, setDestinationCity] = useState('');
  const [startTime, setStartTime] = useState('');
  const [numberOfPassengers, setNumberOfPassengers] = useState(1);
  const [passengerDetails, setPassengerDetails] = useState([]);
  const [coachType, setCoachType] = useState('economy');
  const [totalPrice, setTotalPrice] = useState(0);
  const [selectedSeats, setSelectedSeats] = useState([]);
  const [bookedSeats, setBookedSeats] = useState([]);

  // ✅ Static multiplier (safe outside useEffect)
  const priceMap = {
    'economy': 1,
    'premium-economy': 2,
    'business': 3,
    'first-class': 4,
  };

  // ✅ Safe useEffect with fetch logic inside it
  useEffect(() => {
    const fetchFlightData = async () => {
      try {
        const response = await axios.get(`http://localhost:6001/fetch-flight/${id}`);
        const data = response.data;
        setFlightName(data.flightName);
        setFlightId(data.flightId);
        setBasePrice(data.basePrice);
        setStartCity(data.origin);
        setDestinationCity(data.destination);
        setStartTime(data.departureTime);
      } catch (error) {
        console.error("Error fetching flight data:", error);
      }
    };

    fetchFlightData();
  }, [id]); // ✅ Only depends on id

  // ✅ Recalculate price on changes
  useEffect(() => {
    if (basePrice && numberOfPassengers) {
      const multiplier = priceMap[coachType] || 1;
      setTotalPrice(multiplier * basePrice * numberOfPassengers);
    }
  }, [numberOfPassengers, coachType, basePrice]); // ✅ No need to add priceMap

  const handlePassengerChange = (event) => {
    const value = parseInt(event.target.value);
    setNumberOfPassengers(value);
  };

  const handlePassengerDetailsChange = (index, key, value) => {
    setPassengerDetails((prevDetails) => {
      const updatedDetails = [...prevDetails];
      updatedDetails[index] = { ...updatedDetails[index], [key]: value };
      return updatedDetails;
    });
  };

  const toggleSeatSelection = (seat) => {
    if (bookedSeats.includes(seat)) return;

    if (selectedSeats.includes(seat)) {
      setSelectedSeats(selectedSeats.filter((s) => s !== seat));
    } else {
      if (selectedSeats.length < numberOfPassengers) {
        setSelectedSeats([...selectedSeats, seat]);
      } else {
        alert("You can select only " + numberOfPassengers + " seats");
      }
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Book Flight: {flightName}</h2>
      <p>From: {startCity} → To: {destinationCity}</p>
      <p>Departure Time: {startTime}</p>
      <p>Coach: {coachType}</p>
      <p>Passengers: {numberOfPassengers}</p>
      <p>Total Price: ₹{totalPrice}</p>
    </div>
  );
};

export default BookFlight;
