import React, { useContext, useEffect, useState } from 'react';
import '../styles/LandingPage.css';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { GeneralContext } from '../context/GeneralContext';

const LandingPage = () => {
  const [error, setError] = useState('');
  const [checkBox, setCheckBox] = useState(false);
  const [departure, setDeparture] = useState('');
  const [destination, setDestination] = useState('');
  const [departureDate, setDepartureDate] = useState();
  const [returnDate, setReturnDate] = useState();
  const [Flights, setFlights] = useState([]);
  const { setTicketBookingDate } = useContext(GeneralContext);
  const userId = localStorage.getItem('userId');
  const navigate = useNavigate();

  const slideshowImages = [
    'https://source.unsplash.com/1600x900/?airplane',
    'https://c0.wallpaperflare.com/preview/745/25/116/person-inside-airplane-flying-at-high-altitude-with-view-of-island-during-daytime.jpg',
    'https://media.istockphoto.com/id/155015752/photo/airplane-approaching-landing-on-a-tropical-island.jpg?s=612x612&w=0&k=20&c=oqjo-9j0v6npBHOrzCiUCi71WPuLZ0UIh7r1M8-seSk=',
    'https://images.pexels.com/photos/5096813/pexels-photo-5096813.jpeg',
    'https://png.pngtree.com/thumb_back/fw800/background/20240522/pngtree-airplane-flying-over-rocky-mountain-at-island-against-blue-sky-image_15692983.jpg'
  ];
  const [currentImage, setCurrentImage] = useState(0);

  const airlineLogos = {
    "IndiGo": "/images/indigo.png",
    "Air India": "/images/airindia.png",
    "SpiceJet": "/images/spicejet.png",
    "Vistara": "/images/vistara.png",
    "Ethihad Airways": "/images/ethihad.png",
    "Qatar Airways": "/images/qatar.png",
    "Air France": "/images/air france.png",
    "American Airlines": "/images/american airlines.png",
  };

  useEffect(() => {
    if (localStorage.getItem('userType') === 'admin') {
      navigate('/admin');
    } else if (localStorage.getItem('userType') === 'flight-operator') {
      navigate('/flight-admin');
    }
  }, [navigate]);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImage((prev) => (prev + 1) % slideshowImages.length);
    }, 5000);
    return () => clearInterval(interval);
  }, [slideshowImages.length]);

  const fetchFlights = async () => {
    const date = new Date();
    const date1 = new Date(departureDate);
    const date2 = new Date(returnDate);

    if (checkBox) {
      if (departure && destination && departureDate && returnDate) {
        if (date1 > date && date2 > date1) {
          setError('');
          const response = await axios.get('http://localhost:6001/fetch-flights');
          setFlights(response.data);
        } else {
          setError('Please check the dates');
        }
      } else {
        setError('Please fill all the inputs');
      }
    } else {
      if (departure && destination && departureDate) {
        if (date1 >= date) {
          setError('');
          const response = await axios.get('http://localhost:6001/fetch-flights');
          setFlights(response.data);
        } else {
          setError('Please check the dates');
        }
      } else {
        setError('Please fill all the inputs');
      }
    }
  };

  const handleTicketBooking = async (id, origin, destination) => {
    if (userId) {
      if (origin === departure) {
        setTicketBookingDate(departureDate);
        navigate(`/book-flight/${id}`);
      } else if (destination === departure) {
        setTicketBookingDate(returnDate);
        navigate(`/book-flight/${id}`);
      }
    } else {
      navigate('/auth');
    }
  };

  return (
    <div className="landingPage">
      <div
        className="slideshow-bg"
        style={{ backgroundImage: `url(${slideshowImages[currentImage]})` }}
      ></div>

      <div className="landingHero">
        <div className="landingHero-title">
          <h1 className="banner-h1">Navigating Your Air Travel Options!</h1>
          <p className="banner-p">
            Unleash your travel desires and book extraordinary Flight journeys that will transport you to unforgettable destinations, igniting a sense of adventure like never before.
          </p>
        </div>

        <div className="Flight-search-container input-container mb-4">
          <div className="form-check form-switch">
            <input className="form-check-input" type="checkbox" id="flexSwitchCheckDefault" onChange={(e) => setCheckBox(e.target.checked)} />
            <label className="form-check-label" htmlFor="flexSwitchCheckDefault">Return journey</label>
          </div>

          <div className="Flight-search-container-body">
            <div className="form-floating">
              <select className="form-select" value={departure} onChange={(e) => setDeparture(e.target.value)}>
                <option value="" disabled>Select</option>
                {["Chennai", "Banglore", "Hyderabad", "Mumbai", "Indore", "Delhi", "Pune", "Trivendrum", "Bhopal", "Kolkata", "varanasi", "Jaipur"].map(city => (
                  <option key={city} value={city}>{city}</option>
                ))}
              </select>
              <label htmlFor="floatingSelect">Departure City</label>
            </div>

            <div className="form-floating">
              <select className="form-select" value={destination} onChange={(e) => setDestination(e.target.value)}>
                <option value="" disabled>Select</option>
                {["Chennai", "Banglore", "Hyderabad", "Mumbai", "Indore", "Delhi", "Pune", "Trivendrum", "Bhopal", "Kolkata", "varanasi", "Jaipur"].map(city => (
                  <option key={city} value={city}>{city}</option>
                ))}
              </select>
              <label htmlFor="floatingSelect">Destination City</label>
            </div>

            <div className="form-floating mb-3">
              <input type="date" className="form-control" value={departureDate} onChange={(e) => setDepartureDate(e.target.value)} />
              <label htmlFor="floatingInputstartDate">Journey date</label>
            </div>

            {checkBox && (
              <div className="form-floating mb-3">
                <input type="date" className="form-control" value={returnDate} onChange={(e) => setReturnDate(e.target.value)} />
                <label htmlFor="floatingInputreturnDate">Return date</label>
              </div>
            )}

            <button className="btn btn-primary" onClick={fetchFlights}>Search</button>
            <p className="text-danger mt-2">{error}</p>
          </div>
        </div>

        {Flights.length > 0 && (
          <div className="availableFlightsContainer">
            <h1>Available Flights</h1>
            <div className="Flights">
              {Flights.filter(f => checkBox
                ? (f.origin === departure && f.destination === destination) || (f.origin === destination && f.destination === departure)
                : f.origin === departure && f.destination === destination
              ).map((Flight) => (
                <div className="Flight" key={Flight._id}>
                  <div className="d-flex align-items-center gap-3 mb-2">
                    <img
                      src={airlineLogos[Flight.airline] || "/images/default-logo.png"}
                      alt={`${Flight.airline} Logo`}
                      className="h-12 w-12 object-contain"
                    />
                    <div>
                      <p><b>{Flight.flightName}</b></p>
                      <p><b>Flight Number:</b> {Flight.flightId}</p>
                    </div>
                  </div>
                  <div>
                    <p><b>Start:</b> {Flight.origin}</p>
                    <p><b>Departure Time:</b> {Flight.departureTime}</p>
                  </div>
                  <div>
                    <p><b>Destination:</b> {Flight.destination}</p>
                    <p><b>Arrival Time:</b> {Flight.arrivalTime}</p>
                  </div>
                  <div>
                    <p><b>Starting Price:</b> ₹{Flight.basePrice}</p>
                    <p><b>Available Seats:</b> {Flight.totalSeats}</p>
                  </div>
                  <button className="btn btn-success" onClick={() => handleTicketBooking(Flight._id, Flight.origin, Flight.destination)}>Book Now</button>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>

      <section id="about" className="section-about p-4">
        <div className="container">
          <h2 className="section-title">About Us</h2>
          <p className="section-description">
            This Flight Booking APP is the ultimate digital platform designed to revolutionize the way you book flight tickets. With this app your flight travel experience will be elevated to new heights of convenience and efficiency. Our user-friendly web app empowers travelers to effortlessly discover, explore, and reserve flight tickets based on their unique preferences. Whether you're a frequent commuter or an occasional traveler, finding the perfect flight journey has never been easier.
          </p>
          <span><h5>2025 Flight Finder - &copy; All rights reserved</h5></span>
        </div>
      </section>
    </div>
  );
};

export default LandingPage;
