import axios from 'axios';
import React, { useEffect, useState } from 'react';
import '../styles/FlightBookings.css'; // Ensure this file contains the CSS I gave earlier

const FlightBookings = () => {
  const [userDetails, setUserDetails] = useState();
  const [bookings, setBookings] = useState([]);

  useEffect(() => {
    fetchUserData();
    fetchBookings();
  }, []);

  const fetchUserData = async () => {
    try {
      const id = localStorage.getItem('userId');
      const response = await axios.get(`http://localhost:6001/fetch-user/${id}`);
      setUserDetails(response.data);
    } catch (err) {
      console.error(err);
    }
  };

  const fetchBookings = async () => {
    try {
      const response = await axios.get('http://localhost:6001/fetch-bookings');
      setBookings(response.data.reverse());
    } catch (err) {
      console.error(err);
    }
  };

  const cancelTicket = async (id) => {
    try {
      await axios.put(`http://localhost:6001/cancel-ticket/${id}`);
      alert("Ticket cancelled!");
      fetchBookings();
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="user-bookingsPage">
      {userDetails ? (
        userDetails.approval === 'not-approved' ? (
          <div className="notApproved-box">
            <h3>Approval Required!!</h3>
            <p>Your application is under processing. It needs an approval from the administrator. Please be patient!</p>
          </div>
        ) : userDetails.approval === 'approved' ? (
          <>
            <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>Your Bookings</h2>
            <div className="user-bookings">
              {bookings
                .filter((booking) => booking.flightName === localStorage.getItem('username'))
                .map((booking) => (
                  <div className="user-booking" key={booking._id}>
                    <span>
                      <p><b>Flight:</b> {booking.flightName}</p>
                      <p><b>ID:</b> {booking.flightId}</p>
                    </span>
                    <span>
                      <p><b>From:</b> {booking.departure}</p>
                      <p><b>To:</b> {booking.destination}</p>
                    </span>
                    <span>
                      <p><b>Date:</b> {booking.journeyDate.slice(0, 10)}</p>
                      <p><b>Time:</b> {booking.journeyTime}</p>
                    </span>
                    <span>
                      <p><b>Seats:</b> {booking.seats}</p>
                      <p><b>Price:</b> â‚¹{booking.totalPrice}</p>
                    </span>
                    <span className={booking.bookingStatus === 'cancelled' ? 'user-booking-cancelled' : 'user-booking-status'}>
                      <p><b>Status:</b> {booking.bookingStatus}</p>
                    </span>
                    {booking.bookingStatus === 'confirmed' && (
                      <button className="btn btn-danger" onClick={() => cancelTicket(booking._id)}>Cancel</button>
                    )}
                  </div>
                ))}
            </div>
          </>
        ) : null
      ) : null}
    </div>
  );
};

export default FlightBookings;
