import React, { useContext, useEffect, useState } from 'react';
import '../styles/BookFlight.css';
import { GeneralContext } from '../context/GeneralContext';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import pdfMake from 'pdfmake/build/pdfmake';
import pdfFonts from 'pdfmake/build/vfs_fonts';

pdfMake.vfs = pdfFonts.pdfMake.vfs;

const BookFlight = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { ticketBookingDate } = useContext(GeneralContext);

  const [flightName, setFlightName] = useState('');
  const [flightId, setFlightId] = useState('');
  const [basePrice, setBasePrice] = useState(0);
  const [StartCity, setStartCity] = useState('');
  const [destinationCity, setDestinationCity] = useState('');
  const [startTime, setStartTime] = useState();
  const [email, setEmail] = useState('');
  const [mobile, setMobile] = useState('');
  const [coachType, setCoachType] = useState('');
  const [journeyDate, setJourneyDate] = useState(ticketBookingDate);
  const [numberOfPassengers, setNumberOfPassengers] = useState(0);
  const [passengerDetails, setPassengerDetails] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const [paymentDone, setPaymentDone] = useState(false);
  const [upiId, setUpiId] = useState('');

  const [selectedSeats, setSelectedSeats] = useState([]);
  const totalSeats = 30;
  const bookedSeats = ['A1', 'B2', 'C3'];
  const seatGrid = Array.from({ length: totalSeats }, (_, i) => {
    const row = String.fromCharCode(65 + Math.floor(i / 6));
    const number = (i % 6) + 1;
    return `${row}${number}`;
  });

useEffect(() => {
  const fetchFlightData = async () => {
    const response = await axios.get(`http://localhost:6001/fetch-flight/${id}`);
    setFlightName(response.data.flightName);
    setFlightId(response.data.flightId);
    setBasePrice(response.data.basePrice);
    setStartCity(response.data.origin);
    setDestinationCity(response.data.destination);
    setStartTime(response.data.departureTime);
  };

  fetchFlightData();
}, [id]); // include `id` in dependencies if it can change


  const handlePassengerChange = (event) => {
    const value = parseInt(event.target.value);
    setNumberOfPassengers(value);
  };

  const handlePassengerDetailsChange = (index, key, value) => {
    setPassengerDetails((prevDetails) => {
      const updatedDetails = [...prevDetails];
      updatedDetails[index] = { ...updatedDetails[index], [key]: value };
      return updatedDetails;
    });
  };

  const price = {
    'economy': 1,
    'premium-economy': 2,
    'business': 3,
    'first-class': 4,
  };

// eslint-disable-next-line react-hooks/exhaustive-deps
useEffect(() => {
  if (price[coachType] && basePrice && numberOfPassengers) {
    setTotalPrice(price[coachType] * basePrice * numberOfPassengers);
  }
}, [numberOfPassengers, coachType, basePrice]);


  const toggleSeatSelection = (seat) => {
    if (bookedSeats.includes(seat)) return;
    if (selectedSeats.includes(seat)) {
      setSelectedSeats(selectedSeats.filter((s) => s !== seat));
    } else {
      if (selectedSeats.length < numberOfPassengers) {
        setSelectedSeats([...selectedSeats, seat]);
      } else {
        alert("You can select only " + numberOfPassengers + " seats");
      }
    }
  };

  const simulatePayment = () => {
    if (!upiId) return alert("Enter a valid UPI ID to proceed");
    alert("Payment Successful!");
    setPaymentDone(true);
  };

  const downloadTicketPdf = () => {
    const docDefinition = {
      content: [
        { text: 'Flight Ticket Confirmation', style: 'header' },
        { text: `\nBooking Details\n`, style: 'subheader' },
        `Flight: ${flightName} (${flightId})`,
        `From: ${StartCity} → To: ${destinationCity}`,
        `Journey Date: ${journeyDate}`,
        `Journey Time: ${startTime}`,
        `Seat Class: ${coachType}`,
        `Seats: ${selectedSeats.join(', ')}`,
        `Total Price: ₹${totalPrice}`,
        '\nPassengers:',
        {
          ul: passengerDetails.map((p, i) => `${i + 1}. ${p.name} (Age: ${p.age})`),
        },
        '\nThank you for booking with us!',
      ],
      styles: {
        header: {
          fontSize: 22,
          bold: true,
          alignment: 'center',
          margin: [0, 0, 0, 10],
        },
        subheader: {
          fontSize: 16,
          bold: true,
          margin: [0, 10, 0, 5],
        },
      },
    };

    pdfMake.createPdf(docDefinition).download(`Flight-Ticket-${flightId}.pdf`);
  };

  const bookFlight = async () => {
    if (selectedSeats.length !== numberOfPassengers) {
      return alert("Please select seats equal to number of passengers");
    }
    if (!paymentDone) {
      return alert("Please complete payment before booking");
    }

    const inputs = {
      user: localStorage.getItem('userId'),
      flight: id,
      flightName,
      flightId,
      departure: StartCity,
      journeyTime: startTime,
      destination: destinationCity,
      email,
      mobile,
      passengers: passengerDetails,
      totalPrice,
      journeyDate,
      seatClass: coachType,
      seats: selectedSeats.join(', '),
    };

    await axios.post('http://localhost:6001/book-ticket', inputs)
      .then(() => {
        alert("Booking successful");
        navigate('/bookings');
      })
      .catch(() => {
        alert("Booking failed!!");
      });
  };

  return (
    <div className='BookFlightPage'>
      <div className="BookingFlightPageContainer">
        <h2>Book ticket</h2>
        <span>
          <p><b>Flight Name: </b> {flightName}</p>
          <p><b>Flight No: </b> {flightId}</p>
        </span>
        <span>
          <p><b>Base price: </b> ₹{basePrice}</p>
        </span>

        <span>
          <div className="form-floating mb-3">
            <input type="email" className="form-control" value={email} onChange={(e) => setEmail(e.target.value)} />
            <label>Email</label>
          </div>
          <div className="form-floating mb-3">
            <input type="text" className="form-control" value={mobile} onChange={(e) => setMobile(e.target.value)} />
            <label>Mobile</label>
          </div>
        </span>

        <span className='span3'>
          <div className="form-floating mb-3">
            <input type="number" className="form-control" value={numberOfPassengers} onChange={handlePassengerChange} />
            <label>No of passengers</label>
          </div>
          <div className="form-floating mb-3">
            <input type="date" className="form-control" value={journeyDate} onChange={(e) => setJourneyDate(e.target.value)} />
            <label>Journey date</label>
          </div>
          <div className="form-floating">
            <select className="form-select" value={coachType} onChange={(e) => setCoachType(e.target.value)}>
              <option value="" disabled>Select</option>
              <option value="economy">Economy</option>
              <option value="premium-economy">Premium Economy</option>
              <option value="business">Business</option>
              <option value="first-class">First Class</option>
            </select>
            <label>Seat Class</label>
          </div>
        </span>

        <div className="new-passengers">
          {Array.from({ length: numberOfPassengers }).map((_, index) => (
            <div className='new-passenger' key={index}>
              <h4>Passenger {index + 1}</h4>
              <div className="new-passenger-inputs">
                <div className="form-floating mb-3">
                  <input type="text" className="form-control" value={passengerDetails[index]?.name || ''} onChange={(e) => handlePassengerDetailsChange(index, 'name', e.target.value)} />
                  <label>Name</label>
                </div>
                <div className="form-floating mb-3">
                  <input type="number" className="form-control" value={passengerDetails[index]?.age || ''} onChange={(e) => handlePassengerDetailsChange(index, 'age', e.target.value)} />
                  <label>Age</label>
                </div>
              </div>
            </div>
          ))}
        </div>

        <h4>Select Seats</h4>
        <div className="seat-layout">
          {seatGrid.map((seat) => (
            <div
              key={seat}
              className={`seat ${bookedSeats.includes(seat) ? 'booked' : ''} ${selectedSeats.includes(seat) ? 'selected' : ''}`}
              onClick={() => toggleSeatSelection(seat)}
            >
              {seat}
            </div>
          ))}
        </div>

        <h4>Mock UPI Payment</h4>
        <div className="form-floating mb-3">
          <input type="text" className="form-control" value={upiId} onChange={(e) => setUpiId(e.target.value)} placeholder="yourupi@bank" />
          <label>Enter UPI ID</label>
        </div>
        <button className="btn btn-success mb-3" onClick={simulatePayment}>Simulate Payment</button>

        <h6><b>Total price</b>: ₹{totalPrice}</h6>
        <button className='btn btn-primary' onClick={bookFlight}>Book now</button>

        <button className='btn btn-outline-secondary mt-2' onClick={downloadTicketPdf} disabled={!paymentDone}>Download Ticket PDF</button>
      </div>
    </div>
  );
};

export default BookFlight;
