import axios from 'axios';
import React, { useEffect, useState } from 'react';
import '../styles/AllBookings.css';

const AllBookings = () => {
  const [bookings, setBookings] = useState([]);
  const [backgroundImage, setBackgroundImage] = useState('');

  useEffect(() => {
    fetchBookings();
    setDynamicBackground();
  }, []);

  const fetchBookings = async () => {
    try {
      const response = await axios.get('http://localhost:6001/fetch-bookings');
      setBookings(response.data.reverse());
    } catch (error) {
      console.error('Error fetching bookings:', error);
    }
  };

  const cancelTicket = async (id) => {
    try {
      await axios.put(`http://localhost:6001/cancel-ticket/${id}`);
      alert("Ticket cancelled!");
      fetchBookings();
    } catch (error) {
      console.error('Error cancelling ticket:', error);
    }
  };

  const setDynamicBackground = () => {
    const hour = new Date().getHours();
    if (hour >= 5 && hour < 12) {
      setBackgroundImage("url('https://img.freepik.com/premium-photo/airplane-model-with-laptop-computer-wooden-table-composition-with-toy-plane-table-laptop-travel-planning-concept-globalization-business-processing-concept_96727-2359.jpg')");
    } else if (hour >= 12 && hour < 19) {
      setBackgroundImage("url('https://img.freepik.com/premium-photo/airplane-model-with-laptop-computer-wooden-table-composition-with-toy-plane-table-laptop-travel-planning-concept-globalization-business-processing-concept_96727-2359.jpg')");
    } else {
      setBackgroundImage("url('https://img.freepik.com/premium-photo/airplane-model-with-laptop-computer-wooden-table-composition-with-toy-plane-table-laptop-travel-planning-concept-globalization-business-processing-concept_96727-2359.jpg')");
    }
  };

  return (
    <div
      className="bookings-page"
      style={{ backgroundImage: backgroundImage }}
    >
      <h1 className="bookings-title">All Bookings</h1>
      <div className="bookings-container">
        {bookings.map((booking) => (
          <div className="booking-card" key={booking._id}>
            <p><b>Booking ID:</b> {booking._id}</p>
            <div className="card-section">
              <p><b>Mobile:</b> {booking.mobile}</p>
              <p><b>Email:</b> {booking.email}</p>
            </div>
            <div className="card-section">
              <p><b>Flight ID:</b> {booking.flightId}</p>
              <p><b>Flight Name:</b> {booking.flightName}</p>
            </div>
            <div className="card-section">
              <p><b>From:</b> {booking.departure}</p>
              <p><b>To:</b> {booking.destination}</p>
            </div>
            <div className="card-section">
              <p><b>Passengers:</b></p>
              <ul>
                {booking.passengers.map((p, i) => (
                  <li key={i}>{p.name} (Age: {p.age})</li>
                ))}
              </ul>
            </div>
            {booking.bookingStatus === 'confirmed' && (
              <p><b>Seats:</b> {booking.seats}</p>
            )}
            <div className="card-section">
              <p><b>Booking Date:</b> {booking.bookingDate.slice(0, 10)}</p>
              <p><b>Journey Date:</b> {booking.journeyDate.slice(0, 10)}</p>
            </div>
            <div className="card-section">
              <p><b>Time:</b> {booking.journeyTime}</p>
              <p><b>Total Price:</b> ₹{booking.totalPrice}</p>
            </div>
            <p className={`status ${booking.bookingStatus}`}>
              <b>Status:</b> {booking.bookingStatus}
            </p>
            {booking.bookingStatus === 'confirmed' && (
              <button className="btn btn-danger" onClick={() => cancelTicket(booking._id)}>Cancel Ticket</button>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default AllBookings;
