import axios from 'axios';
import React, { useEffect, useState } from 'react';
import '../styles/AllBookings.css';

const AllBookings = () => {
  const [bookings, setBookings] = useState([]);
  const [selectedBookingId, setSelectedBookingId] = useState(null);
  const [backgroundImage, setBackgroundImage] = useState('');

  useEffect(() => {
    fetchBookings();
    setDynamicBackground();
  }, []);

  const fetchBookings = async () => {
    try {
      const response = await axios.get('http://localhost:6001/fetch-bookings');
      const upcoming = response.data.filter((booking) => {
        const journeyDate = new Date(booking.journeyDate);
        return journeyDate >= new Date(); // only future & today
      });
      setBookings(upcoming.reverse());
    } catch (error) {
      console.error('Error fetching bookings:', error);
    }
  };

  const cancelTicket = async (id) => {
    try {
      await axios.put(`http://localhost:6001/cancel-ticket/${id}`);
      alert("Ticket cancelled!");
      fetchBookings();
    } catch (error) {
      console.error('Error cancelling ticket:', error);
    }
  };

  const setDynamicBackground = () => {
    const hour = new Date().getHours();
    if (hour >= 5 && hour < 12) {
      setBackgroundImage("url('https://images.unsplash.com/photo-1491975749581-7b4f0d58bc55?auto=format&fit=crop&w=1740&q=80')");
    } else if (hour >= 12 && hour < 19) {
      setBackgroundImage("url('https://images.unsplash.com/photo-1602524207799-00e1dc046ba7?auto=format&fit=crop&w=1740&q=80')");
    } else {
      setBackgroundImage("url('https://images.unsplash.com/photo-1504384308090-c894fdcc538d?auto=format&fit=crop&w=1740&q=80')");
    }
  };

  return (
    <div className="bookings-page" style={{ backgroundImage }}>
      <h2 className="bookings-title">Upcoming Departures</h2>

      <div className="bookings-layout">
        {/* LEFT SIDE – Compact Cards */}
        <div className="bookings-list">
          {bookings.map((booking) => (
            <div
              key={booking._id}
              className={`booking-tile ${selectedBookingId === booking._id ? 'active' : ''}`}
              onClick={() =>
                setSelectedBookingId(
                  selectedBookingId === booking._id ? null : booking._id
                )
              }
            >
              <h4>{booking.flightName}</h4>
              <p>{booking.departure} → {booking.destination}</p>
              <p>Date: {booking.journeyDate.slice(0, 10)}</p>
              <p className={`status ${booking.bookingStatus}`}>
                {booking.bookingStatus}
              </p>

              {/* Show Passenger Info on Expand */}
              {selectedBookingId === booking._id && (
                <div className="passenger-details">
                  <h5>Passengers:</h5>
                  <ul>
                    {booking.passengers.map((p, i) => (
                      <li key={i}>
                        {p.name} (Age: {p.age})
                      </li>
                    ))}
                  </ul>
                  <p><b>Seats:</b> {booking.seats}</p>
                  <p><b>Total Price:</b> ₹{booking.totalPrice}</p>

                  {booking.bookingStatus === 'confirmed' && (
                    <button
                      className="btn btn-danger"
                      onClick={() => cancelTicket(booking._id)}
                    >
                      Cancel
                    </button>
                  )}
                </div>
              )}
            </div>
          ))}
        </div>

        {/* RIGHT PANEL (Optional use for more UI later) */}
        <div className="booking-details-placeholder">
          <p>Select a booking to view details</p>
        </div>
      </div>
    </div>
  );
};

export default AllBookings;
