import React, { useEffect, useState, useContext } from 'react';
import '../styles/LandingPage.css';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { GeneralContext } from '../context/GeneralContext';

const LandingPage = () => {
  const [error, setError] = useState('');
  const [checkBox, setCheckBox] = useState(false);
  const [departure, setDeparture] = useState('');
  const [destination, setDestination] = useState('');
  const [departureDate, setDepartureDate] = useState('');
  const [returnDate, setReturnDate] = useState('');
  const [Flights, setFlights] = useState([]);
  const { setTicketBookingDate } = useContext(GeneralContext);
  const userId = localStorage.getItem('userId');
  const navigate = useNavigate();

  const slideshowImages = [
    'https://source.unsplash.com/1600x900/?airplane',
    'https://c0.wallpaperflare.com/preview/745/25/116/person-inside-airplane-flying-at-high-altitude-with-view-of-island-during-daytime.jpg',
    'https://media.istockphoto.com/id/155015752/photo/airplane-approaching-landing-on-a-tropical-island.jpg?s=612x612&w=0&k=20&c=oqjo-9j0v6npBHOrzCiUCi71WPuLZ0UIh7r1M8-seSk=',
    'https://images.pexels.com/photos/5096813/pexels-photo-5096813.jpeg',
    'https://png.pngtree.com/thumb_back/fw800/background/20240522/pngtree-airplane-flying-over-rocky-mountain-at-island-against-blue-sky-image_15692983.jpg'
  ];

  const [currentImage, setCurrentImage] = useState(0);

  useEffect(() => {
    if (localStorage.getItem('userType') === 'admin') {
      navigate('/admin');
    } else if (localStorage.getItem('userType') === 'flight-operator') {
      navigate('/flight-admin');
    }
  }, [navigate]);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImage((prev) => (prev + 1) % slideshowImages.length);
    }, 5000);

    return () => clearInterval(interval);
  }, [slideshowImages.length]);

  const fetchFlights = async () => {
    const today = new Date();
    const depDate = new Date(departureDate);
    const retDate = new Date(returnDate);

    if (checkBox) {
      if (departure && destination && departureDate && returnDate) {
        if (depDate > today && retDate > depDate) {
          setError('');
          const response = await axios.get('http://localhost:6001/fetch-flights');
          setFlights(response.data);
        } else {
          setError('Please check the dates');
        }
      } else {
        setError('Please fill all the inputs');
      }
    } else {
      if (departure && destination && departureDate) {
        if (depDate >= today) {
          setError('');
          const response = await axios.get('http://localhost:6001/fetch-flights');
          setFlights(response.data);
        } else {
          setError('Please check the dates');
        }
      } else {
        setError('Please fill all the inputs');
      }
    }
  };

  const handleTicketBooking = (id, origin, dest) => {
    if (userId) {
      if (origin === departure) {
        setTicketBookingDate(departureDate);
        navigate(`/book-flight/${id}`);
      } else if (dest === departure) {
        setTicketBookingDate(returnDate);
        navigate(`/book-flight/${id}`);
      }
    } else {
      navigate('/auth');
    }
  };

  return (
    <div
      className="landingPage"
      style={{
        backgroundImage: `url(${slideshowImages[currentImage]})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        minHeight: '100vh',
        color: 'white',
      }}
    >
      <div
        className="landingHero"
        style={{
          backgroundColor: 'rgba(0,0,0,0.55)',
          minHeight: '100vh',
          padding: '20px 10%',
          boxSizing: 'border-box',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
        }}
      >
        <div className="landingHero-title" style={{ marginBottom: '30px' }}>
          <h1 className="banner-h1" style={{ fontSize: '2.5rem', marginBottom: '10px' }}>
            Navigating Your Air Travel Options!
          </h1>
          <p className="banner-p" style={{ fontSize: '1.2rem', maxWidth: '700px' }}>
            Unleash your travel desires and book extraordinary Flight journeys that will transport you to unforgettable destinations, igniting a sense of adventure like never before.
          </p>
        </div>

        <div
          className="Flight-search-container input-container mb-4"
          style={{
            backgroundColor: 'rgba(255, 255, 255, 0.95)',
            borderRadius: '10px',
            padding: '20px',
            maxWidth: '600px',
            color: '#333',
            width: '100%',
            boxShadow: '0 0 15px rgba(0,0,0,0.3)',
          }}
        >
          <div className="form-check form-switch" style={{ marginBottom: '15px' }}>
            <input
              className="form-check-input"
              type="checkbox"
              id="flexSwitchCheckDefault"
              onChange={(e) => setCheckBox(e.target.checked)}
              checked={checkBox}
            />
            <label className="form-check-label" htmlFor="flexSwitchCheckDefault" style={{ marginLeft: '10px' }}>
              Return journey
            </label>
          </div>

          <div className="Flight-search-container-body" style={{ display: 'flex', flexDirection: 'column', gap: '15px' }}>
            <select
              className="form-select"
              value={departure}
              onChange={(e) => setDeparture(e.target.value)}
              style={{ padding: '10px', fontSize: '1rem' }}
            >
              <option value="" disabled>
                Departure City
              </option>
              {[
                "Chennai",
                "Banglore",
                "Hyderabad",
                "Mumbai",
                "Indore",
                "Delhi",
                "Pune",
                "Trivendrum",
                "Bhopal",
                "Kolkata",
                "varanasi",
                "Jaipur",
              ].map((city) => (
                <option key={city} value={city}>
                  {city}
                </option>
              ))}
            </select>

            <select
              className="form-select"
              value={destination}
              onChange={(e) => setDestination(e.target.value)}
              style={{ padding: '10px', fontSize: '1rem' }}
            >
              <option value="" disabled>
                Destination City
              </option>
              {[
                "Chennai",
                "Banglore",
                "Hyderabad",
                "Mumbai",
                "Indore",
                "Delhi",
                "Pune",
                "Trivendrum",
                "Bhopal",
                "Kolkata",
                "varanasi",
                "Jaipur",
              ].map((city) => (
                <option key={city} value={city}>
                  {city}
                </option>
              ))}
            </select>

            <input
              type="date"
              className="form-control"
              value={departureDate}
              onChange={(e) => setDepartureDate(e.target.value)}
              style={{ padding: '10px', fontSize: '1rem' }}
            />

            {checkBox && (
              <input
                type="date"
                className="form-control"
                value={returnDate}
                onChange={(e) => setReturnDate(e.target.value)}
                style={{ padding: '10px', fontSize: '1rem' }}
              />
            )}

            <button
              className="btn btn-primary"
              onClick={fetchFlights}
              style={{ marginTop: '10px', padding: '10px', fontSize: '1.1rem' }}
            >
              Search
            </button>
            <p className="text-danger mt-2" style={{ color: 'red' }}>{error}</p>
          </div>
        </div>

        {Flights.length > 0 && (
          <div
            className="availableFlightsContainer"
            style={{
              backgroundColor: 'rgba(255, 255, 255, 0.95)',
              borderRadius: '10px',
              padding: '20px',
              marginTop: '30px',
              maxWidth: '700px',
              width: '100%',
              color: '#333',
              boxShadow: '0 0 15px rgba(0,0,0,0.3)',
              overflowX: 'auto',
            }}
          >
            <h2 style={{ marginBottom: '15px' }}>Available Flights</h2>
            <div className="Flights" style={{ display: 'flex', flexDirection: 'column', gap: '15px' }}>
              {Flights.filter((f) =>
                checkBox
                  ? (f.origin === departure && f.destination === destination) || (f.origin === destination && f.destination === departure)
                  : f.origin === departure && f.destination === destination
              ).map((Flight) => (
                <div
                  key={Flight._id}
                  className="Flight"
                  style={{
                    border: '1px solid #ccc',
                    borderRadius: '8px',
                    padding: '15px',
                    backgroundColor: '#fff',
                  }}
                >
                  <div>
                    <p><b>{Flight.flightName}</b></p>
                    <p><b>Flight Number:</b> {Flight.flightId}</p>
                  </div>
                  <div>
                    <p><b>Start:</b> {Flight.origin}</p>
                    <p><b>Departure Time:</b> {Flight.departureTime}</p>
                  </div>
                  <div>
                    <p><b>Destination:</b> {Flight.destination}</p>
                    <p><b>Arrival Time:</b> {Flight.arrivalTime}</p>
                  </div>
                  <div>
                    <p><b>Starting Price:</b> ₹{Flight.basePrice}</p>
                    <p><b>Available Seats:</b> {Flight.totalSeats}</p>
                  </div>
                  <button
                    className="btn btn-success"
                    onClick={() => handleTicketBooking(Flight._id, Flight.origin, Flight.destination)}
                  >
                    Book Now
                  </button>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>

      <section
        id="about"
        className="section-about p-4"
        style={{
          backgroundColor: '#f7f7f7',
          color: '#333',
          padding: '20px 10%',
          boxSizing: 'border-box',
        }}
      >
        <div className="container">
          <h2 className="section-title">About Us</h2>
          <p className="section-description" style={{ maxWidth: '700px' }}>
            This Flight Booking APP is the ultimate digital platform designed to revolutionize the way you book flight tickets. With this app your flight travel experience will be elevated to new heights of convenience and efficiency. Our user-friendly web app empowers travelers to effortlessly discover, explore, and reserve flight tickets based on their unique preferences. Whether you're a frequent commuter or an occasional traveler, finding the perfect flight journey has never been easier.
          </p>
          <span><h5>2025 Flight Finder - &copy; All rights reserved</h5></span>
        </div>
      </section>
    </div>
  );
};

export default LandingPage;
