import axios from 'axios';
import React, { useEffect, useState } from 'react';
import '../styles/AllBookings.css';

const AllBookings = () => {
  const [bookings, setBookings] = useState([]);
  const [backgroundImage, setBackgroundImage] = useState('');

  useEffect(() => {
    fetchBookings();
    setDynamicBackground();
  }, []);

  const fetchBookings = async () => {
    try {
      const response = await axios.get('http://localhost:6001/fetch-bookings');
      setBookings(response.data.reverse());
    } catch (error) {
      console.error('Error fetching bookings:', error);
    }
  };

  const cancelTicket = async (id) => {
    try {
      await axios.put(`http://localhost:6001/cancel-ticket/${id}`);
      alert("Ticket cancelled!");
      fetchBookings();
    } catch (error) {
      console.error('Error cancelling ticket:', error);
    }
  };

  const setDynamicBackground = () => {
    const hour = new Date().getHours();
    if (hour >= 5 && hour < 12) {
      setBackgroundImage("url('https://img.freepik.com/premium-photo/airplane-model-with-laptop-computer-wooden-table-composition-with-toy-plane-table-laptop-travel-planning-concept-globalization-business-processing-concept_96727-2359.jpg')");
    } else if (hour >= 12 && hour < 19) {
      setBackgroundImage("url('https://img.freepik.com/premium-photo/airplane-model-with-laptop-computer-wooden-table-composition-with-toy-plane-table-laptop-travel-planning-concept-globalization-business-processing-concept_96727-2359.jpg')");
    } else {
      setBackgroundImage("url('https://img.freepik.com/premium-photo/airplane-model-with-laptop-computer-wooden-table-composition-with-toy-plane-table-laptop-travel-planning-concept-globalization-business-processing-concept_96727-2359.jpg')");
    }
  };

return (
  <div className="allFlightsPage">
    <h1>All Flights</h1>
    <div className="allFlights">
      {flights.map((flight) => (
        <div className="allFlights-Flight" key={flight._id}>
          <h4>{flight.flightName}</h4>
          <p><strong>Flight ID:</strong> {flight.flightId}</p>
          <div className="flightDetails">
            <div>
              <p><strong>From:</strong> {flight.origin}</p>
              <p><strong>Departure:</strong> {flight.departureTime}</p>
            </div>
            <div>
              <p><strong>To:</strong> {flight.destination}</p>
              <p><strong>Arrival:</strong> {flight.arrivalTime}</p>
            </div>
          </div>
          <div className="flightMeta">
            <p><strong>Base Price:</strong> â‚¹{flight.basePrice}</p>
            <p><strong>Total Seats:</strong> {flight.totalSeats}</p>
          </div>
        </div>
      ))}
    </div>
  </div>
);

};

export default AllBookings;
