import axios from 'axios';
import React, { useEffect, useState } from 'react';
import '../styles/AllFlights.css';

const AllFlights = () => {
  const [flights, setFlights] = useState([]);

  useEffect(() => {
    fetchFlights();
  }, []);

  const fetchFlights = async () => {
    try {
      const response = await axios.get('http://localhost:6001/fetch-flights');
      setFlights(response.data);
    } catch (error) {
      console.error('Error fetching flights:', error);
    }
  };

  return (
    <div
      className="allFlightsPage"
      style={{
        backgroundImage: `url('https://img.freepik.com/premium-photo/airplane-model-with-laptop-computer-wooden-table-composition-with-toy-plane-table-laptop-travel-planning-concept-globalization-business-processing-concept_96727-2359.jpg')`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        backdropFilter: 'blur(6px)',
        minHeight: '100vh',
        paddingTop: '90px',
      }}
    >
      <h1 style={{ textAlign: 'center', fontSize: '2.2rem', color: '#023e8a', marginBottom: '2rem' }}>
        All Flights
      </h1>

      <div
        className="allFlights"
        style={{
          display: 'flex',
          flexWrap: 'wrap',
          gap: '1.5rem',
          justifyContent: 'center',
          padding: '0 2rem',
        }}
      >
        {flights.map((flight) => (
          <div
            className="allFlights-Flight"
            key={flight._id}
            style={{
              background: 'rgba(255, 255, 255, 0.95)',
              padding: '1rem 1.2rem',
              borderRadius: '1rem',
              width: '300px',
              boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)',
              transition: 'transform 0.2s ease',
            }}
          >
            <h4>{flight.flightName}</h4>
            <p><strong>Flight ID:</strong> {flight.flightId}</p>
            <div style={{ display: 'flex', justifyContent: 'space-between' }}>
              <div>
                <p><strong>From:</strong> {flight.origin}</p>
                <p><strong>Departure:</strong> {flight.departureTime}</p>
              </div>
              <div>
                <p><strong>To:</strong> {flight.destination}</p>
                <p><strong>Arrival:</strong> {flight.arrivalTime}</p>
              </div>
            </div>
            <div>
              <p><strong>Base Price:</strong> â‚¹{flight.basePrice}</p>
              <p><strong>Total Seats:</strong> {flight.totalSeats}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AllFlights;
