import React, { useContext, useEffect, useState } from 'react';
import '../styles/BookFlight.css';
import { GeneralContext } from '../context/GeneralContext';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

const BookFlight = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { ticketBookingDate } = useContext(GeneralContext);

  const [flightInfo, setFlightInfo] = useState({});
  const [email, setEmail] = useState('');
  const [mobile, setMobile] = useState('');
  const [coachType, setCoachType] = useState('');
  const [journeyDate, setJourneyDate] = useState(ticketBookingDate);
  const [numberOfPassengers, setNumberOfPassengers] = useState(0);
  const [passengerDetails, setPassengerDetails] = useState([]);
  const [selectedSeats, setSelectedSeats] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const [bookedSeats, setBookedSeats] = useState([]);
  const [paymentMethod, setPaymentMethod] = useState('');

  const seatClassPrice = {
    'economy': 1,
    'premium-economy': 2,
    'business': 3,
    'first-class': 4
  };

  // Fetch flight info & booked seats
  useEffect(() => {
    axios.get(`http://localhost:6001/fetch-flight/${id}`).then((res) => {
      setFlightInfo(res.data);
    });
    axios.get(`http://localhost:6001/booked-seats/${id}`).then((res) => {
      setBookedSeats(res.data);
    });
  }, [id]);

useEffect(() => {
  const seatClassPrice = {
    'economy': 1,
    'premium-economy': 2,
    'business': 3,
    'first-class': 4
  };

  if (seatClassPrice[coachType] && basePrice && numberOfPassengers) {
    setTotalPrice(seatClassPrice[coachType] * basePrice * numberOfPassengers);
  }
}, [coachType, basePrice, numberOfPassengers]);


  const handlePassengerChange = (e) => {
    const value = parseInt(e.target.value);
    setNumberOfPassengers(value);
    setPassengerDetails(Array(value).fill({ name: '', age: '' }));
  };

  const handlePassengerDetailsChange = (index, key, value) => {
    setPassengerDetails(prev => {
      const updated = [...prev];
      updated[index] = { ...updated[index], [key]: value };
      return updated;
    });
  };

  const handleSeatSelect = (seat) => {
    if (selectedSeats.includes(seat)) {
      setSelectedSeats(selectedSeats.filter(s => s !== seat));
    } else {
      if (selectedSeats.length < numberOfPassengers) {
        setSelectedSeats([...selectedSeats, seat]);
      } else {
        alert(`You can only select ${numberOfPassengers} seats`);
      }
    }
  };

  const bookFlight = async () => {
    const inputs = {
      user: localStorage.getItem('userId'),
      flight: id,
      flightName: flightInfo.flightName,
      flightId: flightInfo.flightId,
      departure: flightInfo.origin,
      journeyTime: flightInfo.departureTime,
      destination: flightInfo.destination,
      email,
      mobile,
      passengers: passengerDetails,
      selectedSeats,
      totalPrice,
      journeyDate,
      seatClass: coachType,
      paymentMethod
    };

    try {
      await axios.post('http://localhost:6001/book-ticket', inputs);
      alert('Booking successful!');
      navigate('/bookings');
    } catch (err) {
      alert('Booking failed!');
    }
  };

  // 6 rows x 6 columns seat map (A1 to F6)
  const seatMap = [];
  const rows = ['A', 'B', 'C', 'D', 'E', 'F'];
  for (let row of rows) {
    for (let i = 1; i <= 6; i++) {
      seatMap.push(`${row}${i}`);
    }
  }

  return (
    <div className="BookFlightPage container">
      <h2 className="mt-3 mb-4">Book Ticket</h2>

      <p><b>Flight:</b> {flightInfo.flightName} ({flightInfo.flightId})</p>
      <p><b>From:</b> {flightInfo.origin} &nbsp;&nbsp; <b>To:</b> {flightInfo.destination}</p>
      <p><b>Departure:</b> {flightInfo.departureTime} &nbsp;&nbsp; <b>Base Price:</b> ₹{flightInfo.basePrice}</p>

      <div className="row mb-3">
        <div className="col-md-6 form-floating mb-2">
          <input type="email" className="form-control" value={email} onChange={(e) => setEmail(e.target.value)} />
          <label>Email</label>
        </div>
        <div className="col-md-6 form-floating mb-2">
          <input type="text" className="form-control" value={mobile} onChange={(e) => setMobile(e.target.value)} />
          <label>Mobile</label>
        </div>
        <div className="col-md-4 form-floating mb-2">
          <input type="number" className="form-control" value={numberOfPassengers} onChange={handlePassengerChange} />
          <label>No. of Passengers</label>
        </div>
        <div className="col-md-4 form-floating mb-2">
          <input type="date" className="form-control" value={journeyDate} onChange={(e) => setJourneyDate(e.target.value)} />
          <label>Journey Date</label>
        </div>
        <div className="col-md-4 form-floating mb-2">
          <select className="form-select" value={coachType} onChange={(e) => setCoachType(e.target.value)}>
            <option value="">Select Seat Class</option>
            <option value="economy">Economy</option>
            <option value="premium-economy">Premium Economy</option>
            <option value="business">Business</option>
            <option value="first-class">First Class</option>
          </select>
          <label>Seat Class</label>
        </div>
      </div>

      <div className="row">
        {passengerDetails.map((_, index) => (
          <div key={index} className="col-md-6">
            <div className="card p-3 mb-3">
              <h5>Passenger {index + 1}</h5>
              <div className="form-floating mb-2">
                <input type="text" className="form-control" value={passengerDetails[index]?.name || ''} onChange={(e) => handlePassengerDetailsChange(index, 'name', e.target.value)} />
                <label>Name</label>
              </div>
              <div className="form-floating">
                <input type="number" className="form-control" value={passengerDetails[index]?.age || ''} onChange={(e) => handlePassengerDetailsChange(index, 'age', e.target.value)} />
                <label>Age</label>
              </div>
            </div>
          </div>
        ))}
      </div>

      <h5 className="mt-4">Select Seats</h5>
      <div className="seat-map">
        {seatMap.map(seat => {
          const isBooked = bookedSeats.includes(seat);
          const isSelected = selectedSeats.includes(seat);
          return (
            <button
              key={seat}
              className={`seat-btn ${isBooked ? 'booked' : isSelected ? 'selected' : ''}`}
              disabled={isBooked}
              onClick={() => handleSeatSelect(seat)}
            >
              {seat}
            </button>
          );
        })}
      </div>

      <div className="form-floating mt-4">
        <select className="form-select" value={paymentMethod} onChange={(e) => setPaymentMethod(e.target.value)}>
          <option value="">Choose Payment Method</option>
          <option value="upi">UPI</option>
          <option value="card">Credit/Debit Card</option>
          <option value="netbanking">Net Banking</option>
        </select>
        <label>Payment Method</label>
      </div>

      <h5 className="mt-3">Total Price: ₹{totalPrice}</h5>
      <button className="btn btn-success mt-3" onClick={bookFlight}>Confirm Booking</button>
    </div>
  );
};

export default BookFlight;
