import React, { useEffect, useState,  } from 'react';
import '../styles/LandingPage.css';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

  const LandingPage = () => {
  const [departure, setDeparture] = useState('');
  const [destination, setDestination] = useState('');
  const [departureDate, setDepartureDate] = useState('');
  const [returnDate, setReturnDate] = useState('');
  const [checkBox, setCheckBox] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();


  const slideshowImages = [
    'https://source.unsplash.com/1600x900/?airplane',
    'https://c0.wallpaperflare.com/preview/745/25/116/person-inside-airplane-flying-at-high-altitude-with-view-of-island-during-daytime.jpg',
    'https://media.istockphoto.com/id/155015752/photo/airplane-approaching-landing-on-a-tropical-island.jpg?s=612x612',
    'https://images.pexels.com/photos/5096813/pexels-photo-5096813.jpeg',
    'https://png.pngtree.com/thumb_back/fw800/background/20240522/pngtree-airplane-flying-over-rocky-mountain-at-island-against-blue-sky-image_15692983.jpg',
  ];
  const [currentImage, setCurrentImage] = useState(0);

  useEffect(() => {
    const userType = localStorage.getItem('userType');
    if (userType === 'admin') navigate('/admin');
    else if (userType === 'flight-operator') navigate('/flight-admin');
  }, [navigate]);

useEffect(() => {
  const interval = setInterval(() => {
    setCurrentImage((prev) => (prev + 1) % slideshowImages.length);
  }, 5000);
  return () => clearInterval(interval);
}, [slideshowImages.length]); // âœ… Added dependency

  const fetchFlights = async () => {
    const today = new Date();
    const depDate = new Date(departureDate);
    const retDate = new Date(returnDate);

    if (checkBox) {
      if (departure && destination && departureDate && returnDate) {
        if (depDate > today && retDate > depDate) {
          setError('');
          await axios.get('http://localhost:6001/fetch-flights'); // Just a mock
        } else setError('Please check the dates');
      } else setError('Please fill all the inputs');
    } else {
      if (departure && destination && departureDate) {
        if (depDate >= today) {
          setError('');
          await axios.get('http://localhost:6001/fetch-flights'); // Just a mock
        } else setError('Please check the dates');
      } else setError('Please fill all the inputs');
    }
  };

  return (
    <div
      className="landingPage"
      style={{
        backgroundImage: `url(${slideshowImages[currentImage]})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        minHeight: '100vh',
        color: 'white',
      }}
    >
      <div
        className="landingHero"
        style={{
          backgroundColor: 'rgba(0,0,0,0)',
          minHeight: '100vh',
          padding: '20px 10%',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          alignItems: 'flex-start',
        }}
      >
        <div style={{ marginBottom: '30px' }}>
          <h1 style={{ fontSize: '4.0rem', marginBottom: '10px' , color: 'blue'}}>
            Navigating Your Air Travel Options!
          </h1>
          <p style={{ fontSize: '1.2rem', maxWidth: '700px' }}>
            Book extraordinary flight journeys to unforgettable destinations.
          </p>
        </div>

        <div
          className="Flight-search-container"
          style={{
            backdropFilter: 'blur(5px)',
            backgroundColor: 'rgba(255, 255, 255, 0.15)',
            borderRadius: '12px',
            padding: '20px',
            width: '600px',
            height: '200px',
            color: '#fff',
            boxShadow: '0 8px 24px rgba(0,0,0,0.3)',
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'space-between',
          }}
        >
          <div className="form-check form-switch" style={{ marginBottom: '10px' }}>
            <input
              className="form-check-input"
              type="checkbox"
              id="flexSwitchCheckDefault"
              onChange={(e) => setCheckBox(e.target.checked)}
              checked={checkBox}
            />
            <label className="form-check-label" htmlFor="flexSwitchCheckDefault">
              Return journey
            </label>
          </div>

          <div style={{ display: 'flex', gap: '10px', width: '100%' }}>
            <select
              className="form-select"
              value={departure}
              onChange={(e) => setDeparture(e.target.value)}
            >
              <option value="" disabled>Departure</option>
              {["Chennai", "Banglore", "Hyderabad", "Mumbai", "Indore", "Delhi", "Pune", "Trivendrum", "Bhopal", "Kolkata", "Varanasi", "Jaipur"].map(city => (
                <option key={city} value={city}>{city}</option>
              ))}
            </select>

            <select
              className="form-select"
              value={destination}
              onChange={(e) => setDestination(e.target.value)}
            >
              <option value="" disabled>Destination</option>
              {["Chennai", "Banglore", "Hyderabad", "Mumbai", "Indore", "Delhi", "Pune", "Trivendrum", "Bhopal", "Kolkata", "Varanasi", "Jaipur"].map(city => (
                <option key={city} value={city}>{city}</option>
              ))}
            </select>

            <input
              type="date"
              className="form-control"
              value={departureDate}
              onChange={(e) => setDepartureDate(e.target.value)}
            />

            {checkBox && (
              <input
                type="date"
                className="form-control"
                value={returnDate}
                onChange={(e) => setReturnDate(e.target.value)}
              />
            )}

            <button className="btn btn-light" onClick={fetchFlights}>
              Search
            </button>
          </div>

          {error && <p style={{ color: 'red', marginTop: '5px' }}>{error}</p>}
        </div>
      </div>
    </div>
  );
};

export default LandingPage;
